import client from './client'

const movieFields = `
_id,
title, 
"actor": actor->{FullName, "name": name.current}
`

const actorFields = `
_id,
name,
"slug": slug.current,
`
export const getMoviesByActor = async (actor) =>{
    const data = await client.fetch(`*[type == 'movie' && actor->name.current==$actor]{${movieFields}}`,
    { actor }
    )
    return data
}

export const getActor = async (slug) =>{
    const data = await client.fetch(`*[type == 'actor' && slug.current == $slug]{${actorFields}}`)
    return data
}
