import client from './client'

const movieFields = `
title, 
"actor": actor->{FullName, "name": name.current}
`

const actorFields = `
_id,
name,
"slug": slug.current,
`
export const getMovie = async () =>{
    const data = await client.fetch(`*[_type == "movie"]{${movieFields}}`
    )
    return data
}

export const getActor = async (slug) =>{
    const data = await client.fetch(`*[_type == "actor" && slug.current == $slug]{${actorFields}}`, 
    { slug }
    )
    return data
}

export async function getMoviesByActor(actor) {
    const data = await client.fetch(
        `*[_type == "movie"]{${movieFields}}`,
        { actor }
    )
    return data
}
