import client from './client'

const movieFields = `
title, 
"actor": actor->{FullName, "name": name.current}
`

const actorFields = `
_id,
name,
"slug": slug.current,
`
export const getMovie = async (actor) =>{
    const data = await client.fetch(`*[type == 'movie' && actor->name.current==$actor]{${movieFields}}`, 
    { actor }
    )
    return data
}

export const getActor = async () =>{
    const data = await client.fetch(`*[type == 'actor']{${actorFields}}`)
    return data
}
