import client from './client'

const movieFields = `
title, 
"actor": actor->{FullName, "name": name.current}
`

const actorFields = `
_id,
name,
"slug": slug.current,
`
export const getMovie = async () =>{
    const data = await client.fetch(`*[type == 'movie']{${movieFields}}`)
    return data
}

export const getActor = async (slug) =>{
    const data = await client.fetch(`*[type == 'actor' && slug.current == $slug]{${actorFields}}`)
    return data
}
