import client from './client'

const movieFields = `
title, 
"actor": actor->name
`

const actorFields = `
_id,
name,
"slug": slug.current,
`
export const getMovie = async (actor) =>{
    const data = await client.fetch(`*[type == 'movie']{${movieFields}}`
    )
    return data
}

export const getActor = async () =>{
    const data = await client.fetch(`*[type == 'actor' && slug.current == $slug]{${actorFields}}`)
    return data
}

export async function getMoviesByActor(actor) {
    const data = await client.fetch(
        `*[_type == "movie" && actor->name.current==$actor]{${movieFields}}`,
        { actor }
    )
    return data
}
